// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract Todo {
  struct STodo {
    string description;
  }

  mapping(address => STodo[]) public todos;

  constructor() public {}

  function addTodo(string memory description)
    public
    returns (uint256 addedIndex)
  {
    // require(msg.sender == owner, "Todo not added by owner.");
    todos[msg.sender].push(STodo(description));
    addedIndex = todos[msg.sender].length;
  }

  function remove(uint256 index) external returns (uint256 newLength) {
    delete todos[msg.sender][index];
    newLength = todos[msg.sender].length;
    // require(msg.sender == owner, "Todo not deleted by owner.");
    // delete todos[index];
    // newLength = todos.length;
  }
}
